import React, { useEffect, useState } from "react";
import "./Recipe.css";

const IngredientInfo = () => {
  const [ ingredientList, setIngredientList ] = useState([]);
  const [ingredient, setIngredient] = useState({
    name: "",
    amount: 0
  })
  const [ showInputIngredient, setShowInputIngredient ] = useState(true);

  const handleIngredientName = ({ target }) => {
    const name = target.value;
    if (name !== "")
      setIngredient({ ...ingredient, name: target.value});
  }

  const handleIngredientAmount = ({ target }) => {
    const amount = target.value;
    if (amount !== 0)
      setIngredient({ ...ingredient, amount: target.value });
  }

  const handleIngredientList = () => {
    ingredient.name !== "" 
    && ingredient.amount !== 0 
    && setIngredientList(oldArray => [...oldArray, {name: ingredient.name, amount: ingredient.amount} ])
  }

  const removeIngredient = (name) => {
    const filteredIngredients = ingredientList.filter((ingredient) => ingredient.name !== name);
    setIngredientList(filteredIngredients);
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: 0
    })
  }, [ingredientList])

  const handleShowInputIngredient = (flag) => {
    if (flag) {
      setShowInputIngredient(true);
      return;
    }
    setShowInputIngredient(false);
  }

  return (
    <>
      {ingredientList.length > 0 && (
        ingredientList.map((ingredient, index) => {
          return (
            <div key={index}>
              <div className="flex-container input-field-col">
                <div className="name-field name-position">{ingredient.name}</div>
              <div>
                <div className="amount-field">{`${ingredient.amount} oz`}</div>
              </div>
              <i className="bi bi-x-circle-fill remove-icon" onClick={() => removeIngredient(ingredient.name)}></i>
              </div>
              <hr />
            </div>
          )
        })
      )}
      {showInputIngredient && (
        <div className="input-row">
          <div className="flex-container input-field-col">
            <input className="name-field" type="text" value={ingredient.name} onChange={handleIngredientName}></input>
            <div>
              <input type="number" value={ingredient.amount} onChange={handleIngredientAmount} className="amount-field" />
              <em>{` oz`}</em>
            </div>
            {ingredientList.length > 0 && <i className="bi bi-x-circle-fill" onClick={handleShowInputIngredient}></i>}
          </div>
          <button type="button" className="btn btn-outline-success add-button" onClick={handleIngredientList}>Done</button>
        </div>
      )}
      <button type="button" className="btn btn-outline-success add-button" onClick={() => handleShowInputIngredient(true)}>Add New Ingredient</button>
    </>
  )
}

export default IngredientInfo;
