import React, { useState } from "react";
import IngredientList from "./IngredientList";
import editIcon from '../../editIcon.png'
import PurchaseList from "../Receipt/PurchaseList";
import "./Recipe.css";

const Description = () => {
  const [ iconToggle, setIconToggle ] = useState(false);
  const [ recipeName, setRecipeName ] = useState("");

  const handleIcon = () => {
    setIconToggle(!iconToggle);
  }

  const handleRecipeName = (e) => {
    setRecipeName(e.tar)
  }

  return (
    <div>     
      { iconToggle ?
        <div>
          <h2>Recipe Name</h2>
          <i class="bi bi-check2-all icon" onClick={handleIcon}></i> 
        </div>
       :
       <div>
        <input type="text" value={recipeName} onChange={handleRecipeName}> {recipeName} </input>
        <i class="bi bi-pencil-fill icon" onClick={handleIcon}></i>
       </div>
      }
      <div className="flex-container">
        <div className="flex-item">
          <IngredientList />
        </div>
        <div className="flex-item">
          <PurchaseList />
        </div>
      </div>
    </div>
)
}

export default Description;
