import React, { useState } from "react";
import "../Recipe/Recipe.css";

const AddPricePerOunce = (props) => {

  const { ingredientList, ingredient, setIngredient } = props;
  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handlePrice = ({ target }) => {
    const price = target.value;
    if (price !== 0.0) {
      setIngredient({ ...ingredient, purchasePrice: price});
      setPriceError(false);
    }
  }

  const handleAmount = ({ target }) => {
    const amount = target.value;
    if (amount !== 0.0) {
      setIngredient({ ...ingredient, purchaseAmount: amount });
      setAmountError(false);
    }
  }

  return (
    <table className="table table-borderless">
      <thead>
        <tr className="heading">
          <th scope="col"></th>
          <th scope="col">Purchase Price</th>
          <th scope="col">Purchase Amount</th>
        </tr>
      </thead>
      <tbody>
        {ingredientList?.map((ingredient, index) => {
          return <tr key={index}>
            <th scope="row">{ingredient.name}</th>
            <td>
              <div>
              <em>{` $ `}</em>
                <input 
                  style={{ borderColor: priceError ? "red" : "" }} 
                  type="number" 
                  step="0.1"
                  onChange={handlePrice} 
                  className="amount-field" 
                  value={ingredient.purchasePrice}
                  placeholder="Price"
                />
                <em>{`\t\t per`}</em>
              </div>
            </td>
            <td>
              <div>
                <input 
                  style={{ borderColor: amountError ? "red" : "" }} 
                  type="number" 
                  step="0.1"
                  onChange={handleAmount} 
                  className="amount-field" 
                  value={ingredient.purchaseAmount}
                  placeholder="Amount"
                />
                <em>{`&nbsp;&nbsp; oz`}</em>
              </div>
            </td>
          </tr>
        })}
      </tbody>
    </table>
  )
}

export default AddPricePerOunce;
