import React, { useState } from "react";
import AddPricePerOunce from "./AddPricePerOunce";
import CalculateBill from "./CalculateBill";
import "../Recipe/Recipe.css";
import { useSelector } from "react-redux";

const PurchaseInfo = (props) => {

  const { cost } = useSelector((state) => state.recipesReducer);

  const { 
    product, 
    setProduct, 
    ingredientList, 
    setIngredientList, 
    setPurchaseList,
    calculateTotalCost
   } = props;

  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handlePurchaseList = () => {
    if (product.purchase_amount !== "" && product.purchase_price !== "")
      setPurchaseList(oldArray => [...oldArray, {
        purchase_amount: product.purchase_amount, purchase_price: product.purchase_price
      } ])
    else {
      product.purchase_amount ===  "" && setAmountError(true);
      product.purchase_price === "" && setPriceError(true);
    }
  }

  return (
    <div className="card card-size border-0">
      <div className="card-body">
        <AddPricePerOunce 
          ingredientList={ingredientList} 
          setIngredientList={setIngredientList} 
          product={product}
          setProduct={setProduct}
          priceError={priceError}
          setPriceError={setPriceError}
          amountError={amountError}
          setAmountError={setAmountError}
          handlePurchaseList={handlePurchaseList}
        />
  <div className="flex-container input-field-col">
    <div className="total-cost">
      Recipe Cost
      <div>{` $ ${cost ? cost : 0} `}</div>
    </div>
  </div>
      </div>
      <div className="card-footer">
        <button 
        type="button" 
        className="btn btn-outline-success add-button"
        onClick={calculateTotalCost}
      >
        Calculate
      </button>
      </div>
    </div>
  )
}

export default PurchaseInfo;
