import React, { useEffect, useState } from "react";
import "./Recipe.css";

const AddIngredient = (props) => {

  const { ingredient, setIngredient, ingredientList, setIngredientList, handleShowInputIngredient } = props;
  const [ nameError, setNameError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handleIngredientName = ({ target }) => {
    const name = target.value;
    if (name !== "") {
      setIngredient({ ...ingredient, name: target.value});
      setNameError(false);
    }
  }

  const handleIngredientAmount = ({ target }) => {
    const amount = target.value;
    if (amount !== 0) {
      setIngredient({ ...ingredient, amount: target.value });
      setAmountError(false);
    }
  }

  const handleIngredientList = () => {
    if (ingredient.name != "" && ingredient.amount != 0 )
      setIngredientList(oldArray => [...oldArray, {name: ingredient.name, amount: ingredient.amount} ])
    else {
      ingredient.amount == 0 && setAmountError(true);
      ingredient.name == "" && setNameError(true);
    }
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: "",
      purchasePrice: "",
      purchaseAmount: "",
    })
  }, [ingredientList])

  return (
    <div className="input-row">
      <div className="flex-container input-field-col">
        <table className="table table-borderless">
          <thead>
            <tr>
              <th scope="col"></th>
              <th scope="col">Purchase Price</th>
              <th scope="col">Purchase Amount</th>
            </tr>
          </thead>
          <tbody>
            {ingredientList?.map((ingredient, index) => {
              return <tr key={index}>
                <th scope="row">{ingredient.name}</th>
                <td>
                  <div>
                  <em>{` $ `}</em>
                    <input 
                      style={{ borderColor: priceError ? "red" : "" }} 
                      type="number" 
                      step="0.1"
                      onChange={handlePrice} 
                      className="amount-field" 
                      value={ingredient.purchasePrice}
                    />
                    <em>{` per `}</em>
                  </div>
                </td>
                <td>
                  <div>
                    <input 
                      style={{ borderColor: amountError ? "red" : "" }} 
                      type="number" 
                      step="0.1"
                      onChange={handleAmount} 
                      className="amount-field" 
                      value={ingredient.purchaseAmount}
                    />
                    <em>{` oz `}</em>
                  </div>
                </td>
              </tr>
            })}
          </tbody>
        </table>
      </div>
      <button type="button" className="btn btn-outline-success add-button" onClick={handleIngredientList}>Done</button>
    </div>
  )
}

export default AddIngredient;
