import { addRecipeApi } from "../../api/recipe";
import { ADD_RECIPE_SUCCESS, ADD_RECIPE_FAILURE } from "../../constants/recipeConstants";
import { SUCCESSFUL, CREATED } from "../../constants/recipeConstants"

const success = (success) => ({
  type: ADD_RECIPE_SUCCESS,
  success,
});

const failure = (error) => ({
  type: ADD_RECIPE_FAILURE,
  error,
});

export const addRecipe =
  (
    recipeName,
    name, 
    amount,
    purchaseAmount,
    purchasePrice,
  ) =>
  (dispatch) => {
    addRecipeApi(
      name, 
      amount,
      purchaseAmount,
      purchasePrice,
    )
    .then((response) => {
      if (response.status === SUCCESSFUL || response.status === CREATED) {
        dispatch(success(response.data));
      } else {
        toast(response);
        dispatch(failure(response));
      }
    })
    .catch((err) => {
      dispatch(failure(err.response.data));
    })
  };
