import React from "react";
import AddPricePerOunce from "./AddPricePerOunce";
import CalculateBill from "./CalculateBill";
import "../Recipe/Recipe.css";
import { useDispatch, useSelector } from "react-redux";
import { addRecipe } from "../../actions/recipe/addRecipe";

const PurchaseInfo = (props) => {

  const dispatch = useDispatch();

  const { cost } = useSelector((state) => state.recipesReducer);

  const { ingredient, setIngredient, ingredientList, setIngredientList, recipeName } = props;

  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handlePrice = ({ target }) => {
    const price = target.value;
    if (price !== 0.0) {
      setIngredient({ ...ingredient, purchase_price: price});
      setPriceError(false);
    }
  }

  const handleAmount = ({ target }) => {
    const amount = target.value;
    if (amount !== 0.0) {
      setIngredient({ ...ingredient, purchase_amount: amount });
      setAmountError(false);
    }
  }



  const calculateTotalCost = () => {
    console.log(ingredientList, "skjdqwb")
    dispatch(addRecipe(
      recipeName, 
      ingredientList
    ));
    if (ingredient.purchase_amount != "" && ingredient.purchase_price != "" )
    setIngredientList(oldArray => [...oldArray, {
      name: ingredient.name, amount: ingredient.amount} ])
    else {
      ingredient.amount == 0 && setAmountError(true);
      ingredient.name == "" && setNameError(true);
    }
  }

  return (
    <div className="card card-size border-0">
      <div className="card-body">
        <AddPricePerOunce 
          ingredientList={ingredientList} 
          setIngredientList={setIngredientList} 
          handleAmount={handleAmount}
          handlePrice={handlePrice}
          priceError={priceError}
          amountError={amountError}
        />
        <CalculateBill 
          calculateTotalCost={calculateTotalCost} 
          cost={cost} />
      </div>
    </div>
  )
}

export default PurchaseInfo;
