import React from "react";
import "../Recipe/Recipe.css";

const CalculateBill = (props) => {
  const { calculateTotalCost, cost } = props;

  const handleIngredientList = () => {
    if (ingredient.purchase_amount != "" && ingredient.purchase_price != "" )
      setIngredientList(oldArray => [...oldArray, {
        purchase_amount: ingredient.purchase_amount, 
        purchase_price: ingredient.purchase_price
      }])
    else {
      ingredient.purchase_amount == 0 && setAmou(true);
      ingredient.name == "" && setNameError(true);
    }
  }
  return (
  <div className="flex-container input-field-col">
    <div className="total-cost">
      Recipe Cost
      <div>{` $ ${cost ? cost : 0} `}</div>
    </div>
    <button 
      type="button" 
      className="btn btn-outline-success add-button"
      onClick={calculateTotalCost}
    >
      Calculate
    </button>
  </div>
  )
}

export default CalculateBill;
