import React, { useState } from "react";
import InputField from "../shared/InputField";
import "./Recipe.css";

const RecipeNameInput = ({ setRecipeName, recipeName }) => {
  const [ iconToggle, setIconToggle ] = useState(false);
  const [ recipeNameError, setRecipeNameError ] = useState(false);

  const handleIcon = () => {
    setIconToggle(!iconToggle);
  }

  const handleRecipeName = ( {target} ) => {
    const name = target.value;
    if (name !== "")
      setRecipeName(name);
    else 
      setRecipeNameError(true);
  }

  return (
    <div>     
      { iconToggle ?
        <div className="flex-container">
          <p className="recipe-input">{recipeName !== "" ? recipeName : "Recipe Name"}</p>
          <i className="bi bi-pencil-fill icon" onClick={handleIcon}></i>
        </div>
       :
       <div className="flex-container top-heading">
        <InputField 
          color={recipeNameError ? "red" : ""}
          inputType="text"
          handleInput={handleRecipeName}
          classes="recipe-input"
          placeholderText="Recipe Name"
          value={recipeName !== "" && recipeName}
        />
        <i className="bi bi-check2-all icon" onClick={handleIcon}></i> 
       </div>
      }
    </div>
  )
}

export default RecipeNameInput;
