import React, { useState } from "react";
import "./Recipe.css";

const IngredientInfo = () => {
  const [ ingredientName, setIngredientName ] = useState("");
  const [ ingredientAmount, setIngredientAmount ] = useState(0);

  const handleIngredientName = ({ target }) => {
    setIngredientName(target.value);
  }

  const handleIngredientAmount = ({ target }) => {
    setIngredientAmount(target.value);
  }

  return (
    <div className="input-row">
      <div className="flex-container"></div>
      <input type="text" value={ingredientName} onChange={handleIngredientName} placeholder="Enter name"></input>
      <input type="number" value={ingredientAmount} onChange={handleIngredientAmount}></input>
    </div>
  )
}

export default IngredientInfo;
