import React, { useState } from "react";
import AddPricePerOunce from "./AddPricePerOunce";
import CalculateBill from "./CalculateBill";
import "../Recipe/Recipe.css";
import { useDispatch, useSelector } from "react-redux";
import { addRecipe } from "../../actions/recipe/addRecipe";

const PurchaseInfo = (props) => {

  const dispatch = useDispatch();

  const { cost } = useSelector((state) => state.recipesReducer);

  const { 
    ingredient, 
    setIngredient, 
    ingredientList, 
    setIngredientList, 
    recipeName
   } = props;

  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handlePurchaseList = () => {
    if (ingredient.purchase_amount != "" && ingredient.purchase_price != 0 )
      setPurchaseList(oldArray => [...oldArray, {
        purchase_amount: ingredient.purchase_amount, purchase_price: ingredient.purchase_price} ])
    else {
      ingredient.purchase_amount == 0 && setAmountError(true);
      ingredient.purchase_price == "" && setPriceError(true);
    }
  }

  const calculateTotalCost = () => {
    console.log(ingredientList, "skjdqwb")
    dispatch(addRecipe(
      recipeName, 
      ingredientList
    ));
  }

  return (
    <div className="card card-size border-0">
      <div className="card-body">
        <AddPricePerOunce 
          ingredientList={ingredientList} 
          setIngredientList={setIngredientList} 
          ingredient={ingredient}
          setIngredient={setIngredient}
          priceError={priceError}
          setPriceError={setPriceError}
          amountError={amountError}
          setAmountError={setAmountError}
        />
        <CalculateBill 
          handlePurchaseList={handlePurchaseList} 
          cost={cost} />
      </div>
    </div>
  )
}

export default PurchaseInfo;
