import React, { useState } from "react";
import "./Recipe.css";

const IngredientInfo = () => {
  const [ ingredientList, setIngredientList ] = useState([]);
  const [ ingredientAmount, setIngredientAmount ] = useState(0);

  const handleIngredientName = ({ target }) => {
    setIngredientList(...ingredientList, target.value);
  }

  const handleIngredientAmount = ({ target }) => {
    setIngredientAmount(target.value);
  }

  return (
    <div className="input-row">
      {ingredientList.length > 0 }
      <div className="flex-container input-field-col">
        <input className="name-field" type="text" value={ingredientName} onChange={handleIngredientName}></input>
        <div>
          <input type="number" value={ingredientAmount} onChange={handleIngredientAmount} className="amount-field" />
          <em>{` oz`}</em>
        </div>
        <i class="bi bi-x-circle-fill"></i>
      </div>
    </div>
  )
}

export default IngredientInfo;
