import React, { useState } from "react";
import IngredientInfo from "./IngredientInfo";
import PurchaseInfo from "../Receipt/PurchaseInfo";
import "./Recipe.css";

const Description = () => {
  const [ iconToggle, setIconToggle ] = useState(false);
  const [ recipeName, setRecipeName ] = useState("");
  const [ ingredientList, setIngredientList ] = useState([]);
  const [ingredient, setIngredient] = useState({
    name: "",
    amount: "",
    purchasePrice: "",
    purchaseAmount: "",
  })

  const handleIcon = () => {
    setIconToggle(!iconToggle);
  }

  const handleRecipeName = ( {target} ) => {
    const name = target.value;
    setRecipeName(name);
  }

  return (
    <div>     
      { iconToggle ?
        <div className="flex-container">
          <p className="recipe-input">{recipeName !== "" ? recipeName : "Recipe Name"}</p>
          <i className="bi bi-pencil-fill icon" onClick={handleIcon}></i>
        </div>
       :
       <div className="flex-container top-heading">
        <input className="recipe-input" type="text" value={recipeName !== "" ? recipeName : ""} placeholder="Recipe Name" onChange={handleRecipeName}></input>
        <i className="bi bi-check2-all icon" onClick={handleIcon}></i> 
       </div>
      }
      <div className="flex-container">
        <div className="flex-item">
          <IngredientInfo 
            ingredient={ingredient} 
            setIngredient={setIngredient} 
            ingredientList={ingredientList} 
            setIngredientList={setIngredientList} 
          />
        </div>
        <div className="flex-item">
          <PurchaseInfo 
            ingredientList={ingredientList}
            setIngredientList={setIngredientList}
            ingredient={ingredient}
            setIngredient={setIngredient}
            recipeName={recipeName}
          />
        </div>
      </div>
    </div>
  )
}

export default Description;
