import React, { useState } from "react";
import AddIngredient from "./AddIngredient";
import IngredientList from "./IngredientList";
import "./Recipe.css";
import "../shared/style.css"

const IngredientInfo = (props) => {
  const { ingredientList, setIngredientList, ingredient, setIngredient } = props;
  const [ showInputIngredient, setShowInputIngredient ] = useState(false);
  const [ nameError, setNameError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handleShowInputIngredient = (flag) => {
    if (flag === "remove") {
      setShowInputIngredient(false);
      return;
    }
    setShowInputIngredient(true);
  }

  const handleIngredientList = () => {
    if (ingredient.name !== "" && ingredient.amount !== "")
      setIngredientList(oldArray => [...oldArray, {name: ingredient.name, amount: ingredient.amount} ])
    else {
      ingredient.amount === "" ? setAmountError(true) : setAmountError;
      ingredient.name === "" ? setNameError(true) : setNameError(false);
    }
  }

  return (
    <div className="card card-size">
      <div className="card-body">
        {ingredientList.length > 0 && (
          <IngredientList 
            ingredientList={ingredientList} 
            setIngredientList={setIngredientList} 
          />
        )} 
        {showInputIngredient && (
          <AddIngredient 
            ingredient={ingredient} 
            setIngredient={setIngredient} 
            ingredientList={ingredientList} 
            handleShowInputIngredient={handleShowInputIngredient}
            nameError={nameError}
            setNameError={setNameError}
            setAmountError={setAmountError}
            amountError={amountError}
          />
        )}
      </div>
      {showInputIngredient ? (
        <div className="card-footer">
          <button 
            type="button" 
            className="btn btn-outline-success add-button" 
            onClick={handleIngredientList}
          >
            Done
          </button>
        </div>
      ) : (
        <div className="card-footer">
          <button 
            type="button" 
            className="btn btn-outline-success add-button" 
            onClick={handleShowInputIngredient}
          >
            Add New Ingredient
          </button>
        </div>
      )}
    </div>
  )
}

export default IngredientInfo;
