import React, { useEffect, useState } from "react";
import "./Recipe.css";
import "../common/style.css"

const AddIngredient = (props) => {

  const { ingredient, setIngredient, ingredientList, handleShowInputIngredient } = props;

  const handleIngredientName = ({ target }) => {
    const name = target.value;
    if (name !== "") {
      setIngredient({ ...ingredient, name: target.value});
      setNameError(false);
    }
  }

  const handleIngredientAmount = ({ target }) => {
    const amount = parseFloat(target.value);
    if (amount !== "" || amount != 0) {
      setIngredient({ ...ingredient, amount });
      setAmountError(false);
    }
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: "",
    })
  }, [ingredientList])

  return (
    <div className="input-row">
      <div className="flex-container input-field-col">
        <input 
          style={{ borderColor: nameError ? "red" : "" }} 
          className="name-field" 
          type="text" 
          onChange={handleIngredientName}
          value={ingredient?.name}
          placeholder="Name"
        />
        <div>
          <input 
            style={{ borderColor: amountError ? "red" : "" }} 
            type="number" 
            step="0.1"
            onChange={handleIngredientAmount} 
            className="amount-field" 
            value={ingredient?.amount}
            placeholder="Amount"
          />
          <em>{` oz`}</em>
        </div>
        {ingredientList.length > 0 && <i className="bi bi-x-circle-fill" onClick={() => handleShowInputIngredient("remove")}></i>}
      </div>
    </div>
  )
}

export default AddIngredient;
