import React, { useEffect, useState } from "react";
import "./Recipe.css";

const AddIngredient = (props) => {

  const { ingredient, setIngredient, ingredientList, setIngredientList, handleShowInputIngredient } = props;
  const [ nameError, setNameError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handleIngredientName = ({ target }) => {
    const name = target.value;
    if (name !== "") {
      setIngredient({ ...ingredient, name: target.value});
      setNameError(false);
    }
  }

  const handleIngredientAmount = ({ target }) => {
    const amount = parseFloat(target.value);
    if (amount !== "" || amount != 0) {
      setIngredient({ ...ingredient, amount });
      setAmountError(false);
    }
  }

  const handleIngredientList = () => {
    if (ingredient.name !== "" && ingredient.amount !== "")
      setIngredientList(oldArray => [...oldArray, {name: ingredient.name, amount: ingredient.amount} ])
    else {
      ingredient.amount === "" && setAmountError(true);
      ingredient.name === "" && setNameError(true);
    }
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: "",
    })
  }, [ingredientList])

  return (
    <>
      <div className="input-row">
        <div className="flex-container input-field-col">
          <input 
            style={{ borderColor: nameError ? "red" : "" }} 
            className="name-field" 
            type="text" 
            onChange={handleIngredientName}
            value={ingredient?.name}
            placeholder="Name"
          />
          <div>
            <input 
              style={{ borderColor: amountError ? "red" : "" }} 
              type="number" 
              step="0.1"
              onChange={handleIngredientAmount} 
              className="amount-field" 
              value={ingredient?.amount}
              placeholder="Amount"
            />
            <em>{` oz`}</em>
          </div>
          {ingredientList.length > 0 && <i className="bi bi-x-circle-fill" onClick={() => handleShowInputIngredient("remove")}></i>}
        </div>
      </div>

      <button 
        type="button" 
        className="btn btn-outline-success add-button" 
        onClick={handleIngredientList}
      >
        Done
      </button>
    </>
  )
}

export default AddIngredient;
