import React, { useState } from "react";
import "./Recipe.css";

const IngredientInfo = () => {
  const [ ingredientList, setIngredientList ] = useState([]);
  const [ingredient, setIngredient] = useState({
    name: "",
    amount: 0
  })

  const handleIngredientName = ({ target }) => {
    setIngredient({ ...ingredient, name: target.value});
  }

  const handleIngredientAmount = ({ target }) => {
    setIngredient({ ...ingredient, amount: target.value });
  }

  const handleIngredientList = () => {
    setIngredientList(oldArray => [...oldArray, {name: ingredient.name, amount: ingredient.amount} ])
  }

  const displayIngredientsList = (ingredient, index) => {
    return (
      <div key={index}>
        <div className="flex-container input-field-col">
          <div>{ingredient.name}</div>
        <div>
          <div>{ingredient.amount}</div>
          <em>{` oz`}</em>
        </div>
        <i className="bi bi-x-circle-fill"></i>
        </div>
      </div>
    )
  }

  console.log(ingredientList);


  return (
    <div className="input-row">
      {ingredientList.length !== 0 && (
        ingredientList.map((ingredient, index) => {
          displayIngredientsList(ingredient, index)
        })
      )}
      <div className="flex-container input-field-col">
        <input className="name-field" type="text" value={ingredient.name} onChange={handleIngredientName}></input>
        <div>
          <input type="number" value={ingredient.amount} onChange={handleIngredientAmount} className="amount-field" />
          <em>{` oz`}</em>
        </div>
        <i className="bi bi-x-circle-fill"></i>
      </div>
      <button type="button" className="btn btn-outline-success add-button" onClick={handleIngredientList}>Done</button>
    </div>
  )
}

export default IngredientInfo;
