import React, { useEffect, useState } from "react";
import "../Recipe/Recipe.css";

const AddPricePerOunce = (props) => {

  const { ingredientList, setIngredientList, ingredient, set } = props;
  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handlePrice = ({ target }) => {
    const name = target.value;
    if (name !== "") {
      setIngredient({ ...ingredient, purchasePrice: target.value});
      setPriceError(false);
    }
  }

  const handleAmount = ({ target }) => {
    const amount = target.value;
    if (amount !== 0) {
      setIngredient({ ...ingredient, purchaseAmount: target.value });
      setPriceError(false);
    }
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: 0,
      purchasePrice: 0,
      purchaseAmount: 0
    })
  }, [ingredientList])

  return (
    <table class="table table-borderless">
      <thead>
        <tr>
          <th scope="col"></th>
          <th scope="col">Purchase Price</th>
          <th scope="col">Purchase Amount</th>
        </tr>
      </thead>
      <tbody>
        {ingredientList?.map((ingredient, index) => {
          <tr key={index}>
            <th scope="row">ingredient.name</th>
            <td></td>
            <td>
              <div>
              <em>{` $ `}</em>
                <input 
                  style={{ borderColor: priceError ? "red" : "" }} 
                  type="number" 
                  onChange={handleIngredientAmount} 
                  className="amount-field" 
                  value={ingredient.price}
                />
                <em>{` per `}</em>
              </div>
            </td>
            <td>
              <div>
                <input 
                  style={{ borderColor: amountError ? "red" : "" }} 
                  type="number" 
                  onChange={handleIngredientAmount} 
                  className="amount-field" 
                  value={ingredient.amount}
                />
                <em>{` oz `}</em>
              </div>
            </td>
          </tr>
        })}
      </tbody>
    </table>
  )
}

export default AddPricePerOunce;
