import React, { useEffect } from "react";
import "../Recipe/Recipe.css";
import InputField from "../shared/InputField";
import "../shared/style.css"

const PurchaseRow = (props) => {
  const { 
    index, 
    product,
    setProduct,
    amountError, 
    priceError, 
    handlePurchaseList,
    setPurchaseList,
    purchaseList
  } = props;

  const { purchase_amount, purchase_price } = product;

  const handlePrice = ({ target }) => {
    const price = parseFloat(target.value);
    setProduct({...product, purchase_price: price, index: target.getAttribute('index')})

    if(!purchaseList[product.index]){
      purchaseList[product.index]= product;
    }
    purchaseList[product.index].purchase_price = price;
    setPurchaseList({...purchaseList})
  }

  const handleAmount = ({ target }) => {
    const amount = parseFloat(target.value);
    setProduct({...product, purchase_amount: amount, index: target.getAttribute('index')})
    if(!purchaseList[index]){
      purchaseList[index]= product;
    }
    purchaseList[product.index].purchase_amount = amount;
    setPurchaseList({...purchaseList})
  }

  const checkProductPrice=(index) => {
    if(purchaseList[index]?.purchase_price){
      return purchaseList[index]?.purchase_price
    }
    return purchase_price !== "" ? purchase_price : "";  
  };
  const checkProductAmount=(index) => {
    if(purchaseList[index]?.purchase_amount){
      return purchaseList[index]?.purchase_amount
    }
    return purchase_amount !== "" ? purchase_amount : "";  
  };

  return (
    <tr key={index}>
      <th scope="row">{ingredient.name}</th>
      <td>
        <div>
          <span>$ &nbsp;</span>
          <InputField 
            color={priceError ? "red" : ""}
            inputType="number"
            step="0.1"
            index={index}
            handleInput={handlePrice}
            classes="amount-field"
            placeholderText="Price"
            onBlur={handlePurchaseList}
            value={checkProductPrice(index)}
          />
          <em>&nbsp;&nbsp; per</em>
        </div>
      </td>
      <td>
        <div>
          <InputField 
            color={amountError ? "red" : ""}
            inputType="number"
            step="0.1"
            index={index}
            handleInput={handleAmount}
            classes="amount-field"
            placeholderText="Amount"
            onBlur={handlePurchaseList}
            value={checkProductAmount(index)}
          />
          <em>&nbsp;&nbsp;&nbsp; oz</em>
        </div>
      </td>
    </tr>
  )
}

export default PurchaseRow;
