import React, { useState } from "react";
import IngredientList from "./IngredientList";
import editIcon from '../../editIcon.png'
import PurchaseList from "../Receipt/PurchaseList";
import "./Recipe.css";

const Description = () => {
  const [ iconToggle, setIconToggle ] = useState(false);
  const [ recipeName, setRecipeName ] = useState("Recipe Name");

  const handleIcon = () => {
    setIconToggle(!iconToggle);
  }

  const handleRecipeName = ( {target} ) => {
    const name = target.value;
    setRecipeName(name);
  }

  return (
    <div>     
      { iconToggle && recipeName ?
        <>
          <h2>Recipe Name</h2>
          <i class="bi bi-check2-all icon" onClick={handleIcon}></i> 
        </>
       :
       <>
        <input type="text" value={recipeName} onChange={handleRecipeName}></input>
        <i class="bi bi-pencil-fill icon" onClick={handleIcon}></i>
       </>
      }
      <div className="flex-container">
        <div className="flex-item">
          <IngredientList />
        </div>
        <div className="flex-item">
          <PurchaseList />
        </div>
      </div>
    </div>
)
}

export default Description;
