import { post } from "../server";

export const addRecipeApi = (
  recipeName,
  name,
  amount,
  purchaseAmount,
  purchasePrice
) => {
  const addRecipeData = {
    recipe:{
      name: recipeName,
      ingredients_attributes: [
        {
          name,
          amount,
          purchase_amount: purchaseAmount,
          purchase_price: purchasePrice
        }
      ]
    }
  };

  return post(
    `http://localhost:3002/api/v1/recipes`,
    addRecipeData
  );
};
