import React, { useEffect, useState } from "react";
import "./Recipe.css";

const AddPricePerOunce = (props) => {

  const { ingredient, setIngredient, ingredientList, setIngredientList } = props;
  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);

  const handlePrice = ({ target }) => {
    const name = target.value;
    if (name !== "") {
      setIngredient({ ...ingredient, purchasePrice: target.value});
      setPriceError(false);
    }
  }

  const handleAmount = ({ target }) => {
    const amount = target.value;
    if (amount !== 0) {
      setIngredient({ ...ingredient, purchaseAmount: target.value });
      setPriceError(false);
    }
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: 0,
      purchasePrice: 0,
      purchaseAmount: 0
    })
  }, [ingredientList])

  return (
    <div className="input-row">
      <div className="flex-container input-field-col">
      <div className="name-field name-position">{ingredient.name}</div>
        <div>
          <input 
            style={{ borderColor: priceError ? "red" : "" }} 
            type="number" 
            onChange={handlePrice} 
            className="amount-field" 
            value={ingredient.purchasePrice}
          />
          <em>{` per`}</em>
        </div>
        <div>
          <input 
            style={{ borderColor: amountError ? "red" : "" }} 
            type="number" 
            onChange={handleAmount} 
            className="amount-field" 
            value={ingredient.purchaseAmount}
          />
          <em>{` oz`}</em>
        </div>
      </div>
    </div>
  )
}

export default AddPricePerOunce;
