import React, { useState } from "react";

function RecipeForm() {
  const [recipe, setRecipe] = useState({
    name: "",
    ingredients_attributes: [{ name: "", amount: 0, purchase_amount: 0, purchase_price: 0 }]
  });

  const handleInputChange = (event, index) => {
    const { name, value } = event.target;
    const ingredients_attributes = [...recipe.ingredients_attributes];
    ingredients_attributes[index][name] = value;
    setRecipe({ ...recipe, ingredients_attributes });
  };

  const handleRemoveIngredient = (index) => () => {
    const ingredients_attributes = [...recipe.ingredients_attributes];
    ingredients_attributes.splice(index, 1);
    setRecipe({ ...recipe, ingredients_attributes });
  };

  const handleAddIngredient = () => {
    const ingredients_attributes = [...recipe.ingredients_attributes, { name: "", amount: 0, purchase_amount: 0, purchase_price: 0 }];
    setRecipe({ ...recipe, ingredients_attributes });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    fetch("http://localhost:3000/recipes", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ recipe })
    })
      .then(response => response.json())
      .then(data => console.log(data))
      .catch(error => console.error(error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="name">Name:</label>
      <input
        type="text"
        id="name"
        name="name"
        value={recipe.name}
        onChange={event => setRecipe({ ...recipe, name: event.target.value })}
      />

      {recipe.ingredients_attributes.map((ingredient, index) => (
        <div key={index}>
          <label htmlFor={`ingredients_attributes[${index}][name]`}>Name:</label>
          <input
            type="text"
            id={`ingredients_attributes[${index}][name]`}
            name={`ingredients_attributes[${index}][name]`}
            value={ingredient.name}
            onChange={event => handleInputChange(event, index)}
          />

          <label htmlFor={`ingredients_attributes[${index}][amount]`}>Amount:</label>
          <input
            type="number"
            id={`ingredients_attributes[${index}][amount]`}
            name={`ingredients_attributes[${index}][amount]`}
            value={ingredient.amount}
            onChange={event => handleInputChange(event, index)}
          />

          <label htmlFor={`ingredients_attributes[${index}][purchase_amount]`}>Purchase amount:</label>
          <input
            type="number"
            id={`ingredients_attributes[${index}][purchase_amount]`}
            name={`ingredients_attributes[${index}][purchase_amount]`}
            value={ingredient.purchase_amount}
            onChange={event => handleInputChange(event, index)}
          />

          <label htmlFor={`ingredients_attributes[${index}][purchase_price]`}>Purchase price:</label>
          <input
            type="number"
            id={`ingredients_attributes[${index}][purchase_price]`}
            name={`ingredients_attributes[${index}][purchase_price]`}
            value={ingredient.purchase_price}
            onChange={event => handleInputChange(event, index)}
          />

          <button type="button" onClick={handleRemoveIngredient(index)}>Remove</button>
        </div>
      ))}

      <button type="button" onClick={handleAddIngredient}>Add ingredient</button>

      <button type="submit">Submit</button>
    </form>
  );
}

export default RecipeForm;
