import React from "react";
import AddPricePerOunce from "./AddPricePerOunce";
import CalculateBill from "./CalculateBill";
import "../Recipe/Recipe.css";
import { useDispatch, useSelector } from "react-redux";
import { addRecipe } from "../../actions/recipe/addRecipe";

const PurchaseInfo = (props) => {

  const dispatch = useDispatch();

  const { cost } = useSelector((state) => state.recipesReducer);

  const { ingredient, setIngredient, ingredientList, setIngredientList, recipeName } = props;

  const calculateTotalCost = () => {
    console.log(ingredientList, "skjdqwb")
    dispatch(addRecipe(
      recipeName, 
      ingredientList
    ));
  }

  const handleIngredientList = () => {
    if (ingredient.purchase_amount != "" && ingredient.purchase_price != "" )
      setIngredientList(oldArray => [...oldArray, {
        purchase_amount: ingredient.purchase_amount, 
        purchase_price: ingredient.purchase_price} ])
    else {
      ingredient.amount == 0 && setAmountError(true);
      ingredient.name == "" && setNameError(true);
    }
  }

  return (
    <div className="card card-size border-0">
      <div className="card-body">
        <AddPricePerOunce 
          ingredientList={ingredientList} 
          setIngredientList={setIngredientList} 
          ingredient={ingredient}
          setIngredient={setIngredient}
        />
        <CalculateBill 
          calculateTotalCost={calculateTotalCost} 
          handleIngredientList={handleIngredientList} 
          cost={cost} />
      </div>
    </div>
  )
}

export default PurchaseInfo;
