import React, { useEffect } from "react";
import InputField from "../shared/InputField";
import "./Recipe.css";
import "../shared/style.css"

const AddIngredient = (props) => {

  const { 
    ingredient, 
    setIngredient, 
    ingredientList, 
    handleShowInputIngredient,
    amountError,
    setAmountError,
    setNameError,
    nameError,
  } = props;

  const handleIngredientName = ({ target }) => {
    const name = target.value;
    setIngredient({ ...ingredient, name: name});
  }

  const handleIngredientAmount = ({ target }) => {
    const amount = parseFloat(target.value);
    setIngredient({ ...ingredient, amount });
  }

  useEffect(() => {
    setIngredient({
      name: "",
      amount: "",
    })
  }, [ingredientList])

  useEffect(() => {
    (ingredient.amount === "" && <ingredient className="amou"></ingredient> == 0) 
      ? setAmountError(true) 
      : setAmountError(false);

      (product.purchase_price === "" && product.purchase_price == 0) 
      ? setPriceError(true) 
      : setPriceError(false);
  }, [product])

  return (
    <div className="input-row">
      <div className="flex-container input-field-col">
        <InputField 
          color={nameError ? "red" : ""}
          inputType="text"
          handleInput={handleIngredientName}
          classes="name-field"
          placeholderText="Name"
          value={ingredient.name !== "" ? ingredient.name : ""}
        />
        <div>
          <InputField 
            color={amountError ? "red" : ""}
            inputType="number"
            step="0.1"
            handleInput={handleIngredientAmount}
            classes="amount-field"
            placeholderText="Amount"
            value={ingredient.amount !== "" ? ingredient.amount : ""}
          />
          <em>{` oz`}</em>
        </div>
        {ingredientList.length > 0 && <i className="bi bi-x-circle-fill" onClick={() => handleShowInputIngredient("remove")}></i>}
      </div>
    </div>
  )
}

export default AddIngredient;
