import React, { useEffect, useState } from "react";
import AddPricePerOunce from "./AddPricePerOunce";
import CalculateBill from "./CalculateBill";
import "../Recipe/Recipe.css";
import { useSelector } from "react-redux";

const PurchaseInfo = (props) => {

  const { cost } = useSelector((state) => state.recipesReducer);

  const { 
    product, 
    setProduct, 
    ingredientList, 
    setIngredientList, 
    setPurchaseList,
    calculateTotalCost,
    purchaseList
   } = props;
  const [ priceError, setPriceError ] = useState(false);
  const [ amountError, setAmountError ] = useState(false);
  const { purchase_amount, purchase_price } = product;

  const handlePurchaseList = () => {
    purchaseList[product.index] = product;
    setPurchaseList({...purchaseList})
  }

  useEffect(() => {
    if(!purchaseList[product.index]){
      setAmountError(true)
      setPriceError(true)
    }
    if (!purchase_amount 
      || purchase_amount === "" 
      || purchase_amount == 0) {
        setAmountError(true)
      } else {
        setAmountError(false) 
      }
    if (!purchase_price 
      || purchase_price === "" 
      || purchase_price == 0) {
        setPriceError(true)
      } else {
        setPriceError(false)
      }
  }, [product])

  return (
    <div className="card card-size border-0">
      <div className="card-body">
        <AddPricePerOunce 
          ingredientList={ingredientList} 
          setIngredientList={setIngredientList} 
          product={product}
          setProduct={setProduct}
          priceError={priceError}
          setPriceError={setPriceError}
          amountError={amountError}
          setAmountError={setAmountError}
          purchaseList={purchaseList}
          handlePurchaseList={handlePurchaseList}
          setPurchaseList={setPurchaseList}
        />
      </div>
      <div className="card-footer">
        <CalculateBill 
          calculateTotalCost={calculateTotalCost} 
          cost={cost} 
        />
      </div>
    </div>
  )
}

export default PurchaseInfo;
