import React from "react";
import "../Recipe/Recipe.css";
import InputField from "../shared/InputField";
import "../shared/style.css"

const PurchaseRow = (props) => {
  const {
    index,
    product,
    setProduct,
    amountError,
    priceError,
    handlePurchaseList,
    setPurchaseList,
    purchaseList,
    ingredient,
  } = props;

  const { purchase_amount, purchase_price } = product;
  let currentProduct = purchaseList[index];

  // const handlePrice = ({ target }) => {
  //   const price = parseFloat(target.value);
  //   setProduct({ ...product, purchase_price: price })

  //   if(!currentProduct){
  //     currentProduct = product;
  //   }
  //   currentProduct.purchase_price = price;
  //   setPurchaseList({...purchaseList})
  // }


  const handlePrice = ({ target }) => {
    const price = parseFloat(target.value);
    const updatedProduct = {
      ...currentProduct,
      purchase_price: price
    };
    if (!currentProduct) {
      currentProduct = product;
    }
    setProduct({ ...product, purchase_price: price });
    if (Array.isArray(purchaseList)) {
      const updatedList = purchaseList.slice();
      setPurchaseList([
        ...purchaseList.slice(0, index),
        updatedProduct,
        ...purchaseList.slice(index + 1)
      ]);
      // continue with code that uses updatedList
    } else {
      console.error('purchaseList is not an array');
    }
  };




  const handleAmount = ({ target }) => {
    const amount = parseFloat(target.value);
    setProduct({ ...product, purchase_amount: amount })
    if (!currentProduct) {
      currentProduct = product;
    }
    currentProduct.purchase_amount = amount;
    setPurchaseList({ ...purchaseList })
  }

  const checkProductPrice = () => {
    if (currentProduct?.purchase_price) {
      return currentProduct?.purchase_price
    }
    return purchase_price !== "" ? purchase_price : "";
  };

  const checkProductAmount = () => {
    if (currentProduct?.purchase_amount) {
      return currentProduct?.purchase_amount
    }
    return purchase_amount !== "" ? purchase_amount : "";
  };

  return (
    <tr key={index}>
      <th scope="row">{ingredient.name}</th>
      <td>
        <div>
          <span>$ &nbsp;</span>
          <InputField
            color={priceError ? "red" : ""}
            inputType="number"
            step="0.1"
            index={index}
            handleInput={handlePrice}
            classes="amount-field"
            placeholderText="Price"
            onBlur={handlePurchaseList}
            value={currentProduct?.purchase_price}
          />
          <em>&nbsp;&nbsp; per</em>
        </div>
      </td>
      <td>
        <div>
          <InputField
            color={amountError ? "red" : ""}
            inputType="number"
            step="0.1"
            index={index}
            handleInput={handleAmount}
            classes="amount-field"
            placeholderText="Amount"
            onBlur={handlePurchaseList}
            value={currentProduct?.purchase_amount}
          />
          <em>&nbsp;&nbsp;&nbsp; oz</em>
        </div>
      </td>
    </tr>
  )
}

export default PurchaseRow;
