import React from "react";
import "../Recipe/Recipe.css";
import "../shared/style.css"

const InputField = (props) => {
  const { 
    value = "", 
    color, 
    inputType, 
    step = "", 
    handleInput, 
    classes, 
    placeholderText, 
    onBlur = null,
    index = 0
  } = props;

  return (
    <input 
      style={{ borderColor: color }} 
      type={inputType} 
      step={step}

      onChange={handleInput(index)} 
      className={classes} 
      placeholder={placeholderText}
      value={value}
      onBlur={onBlur}
    />
  )
}

export default InputField;
